parameters.jsCode:
// Parse Instantly webhook payload and transform for Reply Handler agent
const body = $input.first().json.body;

// Validate required fields
if (!body.event || body.event !== 'reply_received') {
  throw new Error('Invalid event type: expected reply_received');
}

if (!body.lead?.email || !body.email?.body) {
  throw new Error('Missing required fields: lead.email or email.body');
}

// Transform to agent format matching InstantlyWebhookPayloadSchema
const agentPayload = {
  event: 'reply_received',
  timestamp: new Date().toISOString(),
  reply: {
    id: body.email.message_id || `reply_${Date.now()}`,
    message_id: body.email.message_id || null,
    thread_id: body.email.thread_id || null,
    content: body.email.body,
    subject: body.email.subject || 'No subject',
    received_at: body.email.received_at || new Date().toISOString()
  },
  lead: {
    id: body.lead.id || body.lead.email,
    email: body.lead.email,
    first_name: body.lead.first_name || null,
    last_name: body.lead.last_name || null,
    company: body.lead.company_name || 'Unknown',
    title: body.lead.title || null
  },
  campaign: {
    id: body.campaign_id || 'unknown',
    name: body.campaign_name || 'Unknown Campaign',
    sequence_step: body.sequence_step || 1,
    last_sent_template: body.last_template || null
  },
  account_id: body.account_id || 'default',
  workspace_id: body.workspace_id || 'default'
};

return [{ json: agentPayload }];

