// Aggregate final response
const agentResponse = $('Call Reply Handler Agent').first().json;

return [{
  json: {
    success: true,
    data: {
      source: 'instantly',
      reply_id: agentResponse.reply_id,
      category: agentResponse.category,
      tier: agentResponse.tier,
      intent: agentResponse.intent,
      action_type: agentResponse.action_type,
      processed_at: new Date().toISOString(),
      notifications_sent: true
    }
  }
}];
